\COPY (SELECT id, score, body, 0 AS label FROM comments TABLESAMPLE SYSTEM(15) WHERE date >= '2015-01-01' AND date <= '2017-01-15' AND LOWER(subreddit) NOT IN (SELECT subreddit FROM hate_subs) UNION ALL (SELECT id, score, body, 1 AS label FROM comments TABLESAMPLE SYSTEM(30) WHERE date >= '2015-01-01' AND date <= '2017-01-15' AND LOWER(subreddit) IN (SELECT subreddit FROM hate_subs))) TO '/home/snow/rbans/data/main_data_sample.csv' WITH CSV HEADER;
